using Navtrack.Listener.Protocols.Galileosky;
using Xunit;

namespace Navtrack.Listener.Tests.Protocols.Galileosky;

public class GalileoskyProtocolTests : BaseProtocolTests<GalileoskyProtocol, GalileoskyMessageHandler>
{
    [Fact]
    public void DeviceSendsHeadPack_ServerRepliesWithConfirmation()
    {
        ProtocolTester.SendHexFromDevice("011780011102DF03383638323034303035363437383338043200869C");

        Assert.Equal("02869C", ProtocolTester.ReceiveHexInDevice());
    }

    [Fact]
    public void DeviceTest1()
    {
        ProtocolTester.SendHexFromDevice("011780011102DF03383638323034303035363437383338043200869C");

        ProtocolTester.SendHexFromDevice(
            "");

        Assert.True(ProtocolTester.TotalParsedPositions.Count > 0);
    }

